var link=document.querySelector(".hotel-search-button"),popup=document.querySelector(".hotel-search-block-two"),form=popup.querySelector("form"),content=popup.querySelector("[type=text]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.toggle("hotel-search-block-two-closed")}),form.addEventListener("submit",function(e){content.value||(e.preventDefault(),console.log("Нужно заполнить все поля формы"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.add("hotel-search-block-two-closed")});